document.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();var e=this.getAttribute("href"),o=document.querySelector(e);if(o){o=o.getBoundingClientRect().top+window.pageYOffset;try{window.scrollTo({top:o,behavior:"smooth"})}catch(e){console.error("Error occurred during smooth scroll:",e.message,e.stack)}}else console.error("Target element for smooth scroll not found:",e)})})});